import React from "react";

const openContent = (evt, contentName) => {
  let i, tabContent, tabLinks;
};

const Tab = () => {
  return (
    <div className="flex flex-col text-white">
      <div className="flex">
        <button
          className="flex-1 flex flex-col justify-center items-center outline-none border-solid border-4 border-gray-600"
          onClick={openContent("tv")}
        >
          <i class="fas fa-tv fa-2x"></i>
          <span>TV</span>
        </button>
        <button
          className="flex-1 flex flex-col justify-center items-center outline-none border-none"
          onClick={openContent("phone")}
        >
          <i class="fas fa-mobile-alt fa-2x"></i>
          <span>Phone, Tablet &amp; Mobile</span>
        </button>
        <button
          className="flex-1 flex flex-col justify-center items-center outline-none"
          onClick={openContent("desktop")}
        >
          <i class="fas fa-laptop fa-2x"></i>
          <span>Desktop &amp; Laptop</span>
        </button>
      </div>
      <div>
        <div className="hidden flex flex-col justify-center items-center border-solid border-4 border-gray-600">
          TV Content
        </div>
        <div className="hidden flex flex-col justify-center items-center">
          Phone, Tablet Content
        </div>
        <div className="hidden flex flex-col justify-center items-center">
          Desktop Laptop Content
        </div>
      </div>
    </div>
  );
};

export default Tab;
