import React from "react";
import StartButton from "./components/start-button/startButton.component";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import book_img from "./assets/images/lp/book.jpg";
import captain_img from "./assets/images/lp/captain.jpg";
import colette_img from "./assets/images/lp/colette.jpg";
import eco_img from "./assets/images/lp/eco.jpg";
import florida_img from "./assets/images/lp/florida.jpg";
import call_img from "./assets/images/lp/call.jpg";
import BorderedButton from "./components/bordered-button/borderedButton.component";
import Tab from "./components/tab/tab.component";
import Accordion from "./components/accordion/accordion.component";

const API_KEY = process.env.REACT_APP_API_KEY;

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      popularMovies: [],
      nowPlaying: [],
      accordionBlocks: {
        block1: true,
        block2: false,
        block3: false,
        block4: false,
        block5: false,
        block6: false,
      },
      temp: [true, false, false, false, false, false],
    };
  }
  gradientStyle = {
    background:
      "radial-gradient(circle, rgba(121,149,187,1) 0%,rgba(25,31,40,1) 65%, rgba(0,0,0,1) 100%)",
  };
  accordionList = [
    {
      title: "What is Kanopy?",
      content:
        "Kanopy is the best video streaming service for quality, thoughtful entertainment. Find movies, documentaries, foreign films, classic cinema, independent films and educational videos that inspire, enrich and entertain. We partner with public libraries and universities to bring you an ad-free experience that can be enjoyed on your TV, mobile phone, tablets and online.",
    },
    {
      title: "How is Kanopy free for me?",
      content:
        "Kanopy is the best video streaming service for quality, thoughtful entertainment. Find movies, documentaries, foreign films, classic cinema, independent films and educational videos that inspire, enrich and entertain. We partner with public libraries and universities to bring you an ad-free experience that can be enjoyed on your TV, mobile phone, tablets and online.",
    },
    {
      title: "Why do I need a library card or university login to sign up?",
      content:
        "Kanopy is the best video streaming service for quality, thoughtful entertainment. Find movies, documentaries, foreign films, classic cinema, independent films and educational videos that inspire, enrich and entertain. We partner with public libraries and universities to bring you an ad-free experience that can be enjoyed on your TV, mobile phone, tablets and online.",
    },
    {
      title: "Why do I need a library card or university login to sign up?",
      content:
        "Kanopy is the best video streaming service for quality, thoughtful entertainment. Find movies, documentaries, foreign films, classic cinema, independent films and educational videos that inspire, enrich and entertain. We partner with public libraries and universities to bring you an ad-free experience that can be enjoyed on your TV, mobile phone, tablets and online.",
    },
    {
      title: "Why do I need a library card or university login to sign up?",
      content:
        "Kanopy is the best video streaming service for quality, thoughtful entertainment. Find movies, documentaries, foreign films, classic cinema, independent films and educational videos that inspire, enrich and entertain. We partner with public libraries and universities to bring you an ad-free experience that can be enjoyed on your TV, mobile phone, tablets and online.",
    },
    {
      title: "Why do I need a library card or university login to sign up?",
      content:
        "Kanopy is the best video streaming service for quality, thoughtful entertainment. Find movies, documentaries, foreign films, classic cinema, independent films and educational videos that inspire, enrich and entertain. We partner with public libraries and universities to bring you an ad-free experience that can be enjoyed on your TV, mobile phone, tablets and online.",
    },
  ];
  accord_toggle = (index) => {
    const newTemp = [...this.state.temp];
    newTemp[index] = !newTemp[index];
    this.setState({ temp: newTemp });
  };
  componentDidMount() {
    Promise.all([
      fetch(
        `https://api.themoviedb.org/3/movie/popular?api_key=${API_KEY}&language=en-US&page=1`
      ).then((value) => value.json()),
      fetch(
        `https://api.themoviedb.org/3/movie/now_playing?api_key=${API_KEY}&language=en-US&page=1`
      ).then((value) => value.json()),
    ]).then((data) => {
      const popular = data[0];
      const now = data[1];

      this.setState(
        { popularMovies: popular.results, nowPlaying: now.results },
        () => {
          console.log("checking state: ", this.state.popularMovies);
          console.log("checking state: ", this.state.nowPlaying);
        }
      );
    });
  }

  render() {
    const settings = {
      dots: false,
      infinite: true,
      autoplay: true,
      arrows: false,
      fade: true,
      draggable: false,
    };

    const call_action_settings = {
      dots: false,
      arrows: false,
      infinite: true,
      slidesToShow: 4,
      slidesToScroll: 1,
      autoplay: true,
      speed: 5000,
      autoplaySpeed: 0,
      cssEase: "linear",
    };

    return (
      <div className="grid grid-cols-1 bg-black">
        <section className="relative">
          {/* <button>Log in</button> */}
          <Slider {...settings} className="w-full z-0">
            <div className="w-full h-screen">
              <img
                className="w-full h-full object-cover"
                alt="film_img"
                src={book_img}
              />
            </div>
            <div className="w-full h-screen">
              <img
                className="w-full h-full object-cover"
                alt="film_img"
                src={captain_img}
              />
            </div>
            <div className="w-full h-screen">
