import React from "react";

const Accordion = (props) => {
  const { title, content, expand, onClick } = props;
  return (
    <div>
      <dt className={expand ? "bg-blue-500" : "bg-red-500"}>{title}</dt>
    </div>
  );
};

export default Accordion;
